Method.Implementation:
/// Enforce:
/// [ SqlComputeCode = {set {*} = ##class(class).propertyGetStatic({ID})}, SqlComputed, Transient ];

set property.Transient = $$$YES
set property.SqlComputed = $$$YES
set property.SqlComputeCode = "set {*} = ##class(" _class.Name _ ")." _ property.Name _ "GetStatic({ID})"

quit $$$OK


if ##class(%Dictionary.MethodDefinition).IDKEYExists(class.Name, methodName, .methodId) {
	set method = ##class(%Dictionary.MethodDefinition).%OpenId(methodId)
} else {
	set method = ##class(%Dictionary.MethodDefinition).%New(class.Name _ ":" _ methodName)
}
quit method
